import React from 'react'

import CustomReply from '../Replys/CustomReply'

//map all replies
const Replys = ({ replys }) => {
    return (
        <div>
            {(replys.map(({ id, content, posted, user: { username, profile: { image } } }) => {
                return (
                    <CustomReply 
                        key={id}
                        id={id}
                        content={content}
                        posted={posted}
                        username={username}
                        image={image}
                    />
                )
            }))}
        </div>
    )
}

export default Replys
