import React, { useState, useRef } from 'react'
import { useQuery, useMutation } from '@apollo/react-hooks'
import { Form } from 'semantic-ui-react'

import { POST_LIST_QUERY } from '../../api/post'
import { POST_FILTER_MUTATION  } from '../../api/post'

import FilteredPosts from './FilteredPosts';
import CustomPosts from './CustomPosts'


const Posts = () => {
    const { data: postsData } = useQuery(POST_LIST_QUERY)
    const [ postFilter, { data: filteredData }] = useMutation(POST_FILTER_MUTATION)
    const searchRef = useRef()

    //Find posts that starts with user input in search bar
    const handleOnChange = (event) => {
        const searchValue = searchRef.current.value
        postFilter({ variables: { title: searchValue } })
    }

    return (
        <div className="posts">
            <div className="search_bar_container">
                <Form onChange={handleOnChange}>
                    <Form.Field>
                        <input ref={searchRef} placeholder='Search...' />
                    </Form.Field>
                </Form>
            </div>
            {(filteredData || postsData) ? (
                <div>
                    {/*If user has written something in search bar*/}
                    {(filteredData) ? (
                        <FilteredPosts filteredData={filteredData} />
                    ) : (
                        <>
                            {/*Otherwise get all posts*/}
                            <CustomPosts postsData={postsData} />
                        </>
                    )}
                </div>
            ) : null}
            {(postsData === undefined) ? (
                <h1>Loading....</h1>
            ) : null}
        </div>
    )
}

export default Posts
