import React, { useState, useRef, useEffect } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'
import { Button, Form, Message } from 'semantic-ui-react'

import { USER_REGISTER_MUTATION } from '../api/user'

//const authenticationList = ['username', 'email', 'password', 'passwordConfirm']

const Register = () => {
  const usernameRef = useRef()
  const emailRef = useRef()
  const passwordRef = useRef()
  const passwordConfirmRef = useRef()
  
  const history = useHistory()
  const [ allowButton, setAllowButton ] = useState(false)
  const [ message, setMessage ] = useState({type: '', text: []}) 
  const [ registerUser, { data } ] = useMutation(USER_REGISTER_MUTATION);

  useEffect(() => {
    setMessage({type: '', text: []}) //Reset previous state
    
    if (data) {
      const messageBoolean = data.register.success
      
      if (messageBoolean === false) {
        const errors = Object.entries(data.register.errors)
        
        errors.map(error => {
          const messageError = error[1][0].message
          
          return setMessage((prevState) => ({
            type: 'error',
            text: [...prevState.text, messageError]
            
          }))
        })
      }
      
      else {
        history.push('/login')
      }
    }
  }, [data])
  
  const handleOnClick = () => {
    const usernameValue = usernameRef.current.value
    const emailValue = emailRef.current.value
    const passwordValue = passwordRef.current.value
    const passwordConfirmValue = passwordConfirmRef.current.value

    registerUser({ variables: {
      email: emailValue, username: usernameValue,
      password1: passwordValue, password2: passwordConfirmValue
    }})
  }
  
  const checkForm = () => {
    if (
      usernameRef.current.value && emailRef.current.value && 
      passwordRef.current.value && passwordConfirmRef.current.value
    ) {
      setAllowButton(true)
    }

    else {
      setAllowButton(false)
    }
  }


  return (
    <div className="register-container">
      {(message.type) ? (
        <Message
          className="message-container"
          error
          header="There was some errors with your submission"
          list={message.text}
        />
        ) : null }
      <Form onChange={checkForm}>
        <Form.Field>
          <label>Username</label>
          <input ref={usernameRef} placeholder='Username' />
        </Form.Field>
        <Form.Field>
          <label>Email</label>
          <input ref={emailRef} placeholder='Email' />
        </Form.Field>
        <Form.Field>
          <label>Password</label>
          <input type="password" ref={passwordRef} placeholder='Password' />
        </Form.Field>
        <Form.Field>
          <label>Confirm Password</label>
          <input type="password" ref={passwordConfirmRef} placeholder='Confirm Password' />
        </Form.Field>
        <Form.Field>
          <p className="text-muted">Already have an account? <a href="/login" className="ml-2" >Sign in</a></p>
        </Form.Field>
        {(allowButton) ? (
          <Button onClick={handleOnClick} className="register-button" type='button' primary>Register</Button>
        ) : (
          <Button disabled onClick={handleOnClick} className="register-button" type='button' primary>Register</Button>
        )}
      </Form>
    </div>
  )
}

export default Register
