import React from 'react'
import { Item } from 'semantic-ui-react'
import { useHistory } from 'react-router-dom'
import './App.css';

const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const Post = ({ movie, openPostDetail }) => {
    const urlOfPost = `/${movie.id}`
    const history = useHistory()

    const handleOnClick = () => {
        history.push(urlOfPost)
        openPostDetail(movie)
    }

    return (
        <Item.Group>
            <Item onClick={handleOnClick} className="post">
                <Item.Image size='small' src={DEFAULT_IMAGE} />

                <Item.Content>
                    <Item.Header as='a'>{movie.title}</Item.Header>
                    <Item.Meta>User id: {movie.userId}</Item.Meta>
                </Item.Content>
            </Item>
        </Item.Group>
    )
}

export default Post
