import React, { useState, useEffect, useRef } from 'react'
import { Item, Button, Icon, Label } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useParams } from 'react-router-dom'

import { POST_FIND_MUTATION, POST_LIKE_MUTATION } from '../../api/post'
import { COMMENT_ADD_MUTATION } from '../../api/comment'

import ProfileButtons from './ProfileButtons'
import CommentsContainer from './CommentsContainer'
import styles from '../Posts.module.css'


const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const PostDetail = () => {
    const { id } = useParams()
    
    const user = localStorage.getItem('user')
    const [ likes, setLikes ] = useState(0)
    
    const [ postFind, { data: detailData } ] = useMutation(POST_FIND_MUTATION)
    const [ postLike ] = useMutation(POST_LIKE_MUTATION)
    
    //Set likes on given fetched data
    useEffect(() => {
        if (detailData) {
            setLikes(detailData.findPost.post.totalLikes)
        }
    }, [detailData])

    //Fetch data everytime user enter post
    useEffect(() => {
        //Prevents from getting error
        if (id !== 'login' && id !== 'profile') {
            postFind({ variables: { id: id } })
        }
    }, [id])


    //Fetch to access users likes
    const handleOnLike = () => {
        postLike({ variables: { id: id, user: user } })
        window.location.reload(false);
    }


    return (
        <div>
            {(detailData) ? (
            <Item.Group>
                <Item className={styles.post_detail_item}>
                    <Item.Image size='small' src={DEFAULT_IMAGE} />
                    <Item.Content className={styles.post_content}>
                        <Item.Header>{detailData.findPost.post.title}</Item.Header>
                        <Item.Meta className={styles.post_user}>
                            <a href={`profile/${detailData.findPost.post.user.username}`}>
                                {detailData.findPost.post.user.username}
                            </a>
                        </Item.Meta>
                        <Item.Meta className={styles.post_user}>{detailData.findPost.post.posted}</Item.Meta>
                        <Item.Description className={styles.post_textfield}>{detailData.findPost.post.content}</Item.Description>
                            <div className={styles.post_detail_buttons_container}>
                                <Button as='div' labelPosition='right'>
                                    <Button onClick={handleOnLike} color='red'>
                                        <Icon name='heart' />
                                        Like
                                    </Button>
                                    <Label as='a' basic color='red' pointing='left'>
                                        {likes}
                                    </Label>
                                </Button>
                                {/*Delete and edit button (if it is user post)*/}
                                <ProfileButtons id={id} detailData={detailData} />
                            </div>
                        {/*CommentsContainer, including reply form and button*/}
                        <CommentsContainer id={id} detailData={detailData} />
                    </Item.Content>
                </Item>
            </Item.Group>
        ) : null} 
        </div>
    )
}

export default PostDetail
