import React from 'react'
import { Item, Button } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'

import { POST_DELETE_MUTATION, POST_FIND_MUTATION } from '../post_query'

const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const PostDetail = () => {
    const { id } = useParams()
    const history = useHistory()
    const user = localStorage.getItem('user')
    const [ postDelete, { data: deleteData } ] = useMutation(POST_DELETE_MUTATION)
    const [ postFind, { data: detailData } ] = useMutation(POST_FIND_MUTATION)

    const handleOnDelete = () => {
        postDelete({ variables: {id: id } })
        history.push('/')
    }

    const handleOnEdit = () => {
        postFind({ variables: { id: id } })
        console.log(detailData)
        //history.push(`editPost/${id}`)
    }

    return (
        <div>
           <Item.Group>
                <Item className="post">
                    <Item.Image size='small' src={DEFAULT_IMAGE} />

                    <Item.Content>
                        <Item.Header as='a'></Item.Header>
                        <Item.Meta>User: </Item.Meta>
                <Button className="submit-button" type='button' primary>Edit</Button>
                <Button className="submit-button" type='button' primary>Edit</Button>
                    </Item.Content>
                </Item>
            </Item.Group>
        </div>
    )
}

export default PostDetail
