import React, { useEffect } from 'react'
import { Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'

const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                console.log(user)
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])


    return (
        <div className="profile-container">
            <Image src='https://react.semantic-ui.com/images/wireframe/square-image.png' size='medium' circular />
        </div>
    )
}

export default Profile
