import React, { useState } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom'
import { useQuery, useMutation } from '@apollo/react-hooks'

//Components
import Navbar from './components/Navbar'
//Posts
import Posts from './components/Posts/Posts'
import PostDetail from './components/Posts/PostDetail'
//Authentication
import Login from './components/Authentication/Login'
import Register from './components/Authentication/Register'

import { POST_LIST_QUERY, USER_REFRESH_TOKEN_MUTATION } from './components/query'

function App () {
  const token = localStorage.getItem('token')
  const [ canAccessPosts, setCanAcesssPosts ] = useState(false)
  const [ postDetail, setPostDetail ] = useState({})
  //const [ message, setMessage ] = useState()
  const { data: postData  } = useQuery(POST_LIST_QUERY)
  const [ verifyTok, { data: dataToken } ] = useMutation(USER_REFRESH_TOKEN_MUTATION)


  const openPostDetail = (movie) => {
    setPostDetail(movie)
  }

  if (token) {
    verifyTok({ variables: {token: token} })
    if (dataToken && dataToken.verifyToken.success) {
      const boolean = dataToken.verifyToken.success
      if (boolean === true) {
        setCanAcesssPosts(true)
      }
    } 
  }

  return (
    <div className="App">
        <Navbar />

        <Switch>
          <Route path="/login" component={() => <Login />} />
          <Route path="/register" component={() => <Register />} />
          {(canAccessPosts === true) ? (
            <Switch>
              <Route path="/:id" component={() => <PostDetail postDetail={postDetail} />} />
              {(postData) ? (
                <Route path="/" component={() => <Posts data={postData} openPostDetail={openPostDetail} />} />
                ) : null }
            </Switch>  
          ) : (
            <Redirect to='/login' />
          )}
        </Switch>
    </div>
  );
}

export default App;
