import React, { useState, useRef, useEffect } from 'react'
import { Button, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'

import { POST_EDIT_MUTATION, POST_FIND_MUTATION } from '../Api/post' 

const PostEdit = () => {    
    const [ allowButton, setAllowButton ] = useState(false)
    const [ titleInput, setTitleInput ] = useState('')
    const [ textAreaInput, setTextAreaInput ] = useState('')
    
    const { id } = useParams()
    const user = localStorage.getItem('user')
    const history = useHistory()
    const [ postEdit ] = useMutation(POST_EDIT_MUTATION) 
    const [ postFind, { data: detailData } ] = useMutation(POST_FIND_MUTATION)

   //everytime id changes -> find post info of that id 
    useEffect(() => {
        postFind({variables: { id: id }})
    }, [id])
    
    //set title and textarea to set default value in forms
    useEffect(() => {
        if (detailData) {
            setTitleInput(detailData.findPost.post.title)
            setTextAreaInput(detailData.findPost.post.content)
        }
    }, [detailData])

    const handleOnSubmit = () => {
        postEdit({ variables: { id: id, title: titleInput, content: textAreaInput } })
        history.push('/')
    }
    
    //if title and textarea are filled -> undisable button
    const checkForm = () => {
        if (titleInput !== '' && textAreaInput !== '') {
            setAllowButton(true)
        }
        
        else {
            setAllowButton(false)
        }
    }
    
    //set title on the new value
    const onTitleChange = (value) => {
        setTitleValue(value)
    }
    
    //set textarea on the new value
    const onTextAreaChange = (value) => {
        setTextAreaValue(value)
    }
    
    
    return (
        <div className="post-create-container">
            {(detailData && detailData.findPost.post.user.username === user) ? (
                <Form onChange={checkForm}>
                    <Form.Field>
                        <label>Title</label>
                        <input onChange={event => onTitleChange(event.target.value)} value={titleValue} ref={titleRef} placeholder="Title" />
                    </Form.Field>
                    <Form.Field>
                        <label>Content</label>
                        <textarea onChange={event => onTextAreaChange(event.target.value)} value={textareaValue} ref={contentRef} placeholder='Enter something...' />
                    </Form.Field>
                    {(allowButton) ? (
                        <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Edit</Button>
                    ) : (
                        <Button disabled onClick={handleOnSubmit} className="submit-button" type='button' primary>Edit</Button>
                    )}
                </Form>
            ) : null }
        </div> 
    )
}

export default PostEdit
