import React, { useEffect } from 'react'
import '../App.css';

import Post from './Post'

const Posts = ({ data, openPostDetail }) => {
    
    useEffect(() => {
        window.location.reload(false);
    })

    return (
        <div className="posts">
            {data.allPosts.map((movie, index) => {
                return (
                    <Post 
                        key={index} 
                        movie={movie}
                        openPostDetail={openPostDetail}
                    />
                )
            })}
        </div>
    )
}

export default Posts
