import React, { useState, useEffect, useRef } from 'react'
import { Item, Button, Icon, Label, Comment, Header, Form } from 'semantic-ui-react'
import { useMutation, useQuery } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'

import { POST_DELETE_MUTATION, POST_FIND_MUTATION, POST_LIKE_MUTATION } from '../api/post'
import { USER_PROFILE_LIST_QUERY} from '../api/user'
import { COMMENT_ADD_MUTATION } from '../api/comment'

import CustomComment  from '../Comments/CustomComment'

const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const PostDetail = () => {
    const { id } = useParams()
    
    const history = useHistory()
    const user = localStorage.getItem('user')
    const [ likes, setLikes ] = useState(0)
    
    const [ postDelete ] = useMutation(POST_DELETE_MUTATION)
    const [ postFind, { data: detailData } ] = useMutation(POST_FIND_MUTATION)
    const [ postLike ] = useMutation(POST_LIKE_MUTATION)
    const [ commentAdd ] = useMutation(COMMENT_ADD_MUTATION)

    const commentRef = useRef()
    const [ allowButton, setAllowButton ] = useState(false)
    
    useEffect(() => {
        if (detailData) {
            setLikes(detailData.findPost.post.totalLikes)
        }
    }, [detailData])

    useEffect(() => {
        if (id !== 'login' && id !== 'profile') {
            postFind({ variables: { id: id } })
        }
    }, [id])

    const handleOnDelete = () => {
        postDelete({ variables: { id: id } })
        history.push('/')
    }

    const handleOnEdit = () => {
        history.push(`editPost/${id}`)
    }

    const handleOnLike = () => {
        postLike({ variables: { id: id, user: user } })
        window.location.reload(false);
    }

    const handleOnComment = () => {
        const contentValue = commentRef.current.value
        commentAdd({ variables: { id: id, user: user, content: contentValue } })
        window.location.reload(false);
    }

    const checkForm = () => {
        if (commentRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    return (
        <div>
            {(detailData) ? (
            <Item.Group>
                <Item className="post-detail-item">
                    <Item.Image size='small' src={DEFAULT_IMAGE} />
                    <Item.Content className="post-content">
                        <Item.Header>{detailData.findPost.post.title}</Item.Header>
                        <Item.Meta className="post-user">{detailData.findPost.post.user.username}</Item.Meta>
                        <Item.Meta className="post-user">{detailData.findPost.post.posted}</Item.Meta>
                        <Item.Description className="post-textfield">{detailData.findPost.post.content}</Item.Description>
                            <div className="post-detail-buttons-container">
                                <Button as='div' labelPosition='right'>
                                    <Button onClick={handleOnLike} color='red'>
                                        <Icon name='heart' />
                                        Like
                                    </Button>
                                    <Label as='a' basic color='red' pointing='left'>
                                        {likes}
                                    </Label>
                                </Button>                
                                {(user === detailData.findPost.post.user.username) ? (
                                    <>
                                        <Button onClick={handleOnEdit} className="post-detail-edit-button" color="teal">Edit</Button>
                                        <Button onClick={handleOnDelete} className="post-detail-delete-button" color="red">Delete</Button>
                                    </>
                                ) : null }
                            </div>
                            <div className="comments-container">
                            {(detailData.findPost.post.commentSet) ? (
                                <Comment.Group>
                                    <Header as='h3' dividing>
                                        Comments
                                    </Header>
                                    {(detailData.findPost.post.commentSet.map(({ id, content, posted, user: { username }, replySet }) => {
                                        console.log(detailData)
                                        return (
                                            <CustomComment 
                                                key={id}
                                                id={id}
                                                replys={replySet}
                                                content={content}
                                                posted={posted}
                                                username={username}
                                            />
                                        )
                                    }))}
                                </Comment.Group>
                            ) : null }
                            <div className="comment-create-container">
                                <Form onChange={checkForm} reply>
                                    <textarea ref={commentRef} className="post-create-content-container" placeholder='Enter something...' />
                                    {(allowButton) ? (
                                        <Button onClick={handleOnComment} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                                    ) : (
                                        <Button disabled onClick={handleOnComment} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                                    )}
                                </Form>
                            </div>
                        </div>
                    </Item.Content>
                </Item>
            </Item.Group>
        ) : null} 
        </div>
    )
}

export default PostDetail
