import React, { useState } from 'react';
import { Route, Switch } from 'react-router-dom'
import { useQuery } from '@apollo/react-hooks'
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from '@apollo/react-hooks';

//Components
import Navbar from './components/Navbar'
//Posts
import Posts from './components/Posts/Posts'
import PostDetail from './components/Posts/PostDetail'
//Authentication
import Login from './components/Authentication/Login'
import Register from './components/Authentication/Register'

import { POST_LIST_QUERY } from './components/query'


const apiUserClient = new ApolloClient({
  uri: 'http://127.0.0.1:8000/users/'
})

const apiPostClient = new ApolloClient({
  uri: 'http://127.0.0.1:8000/posts/'
})

function App () {
  const [ postDetail, setPostDetail ] = useState({})
  const { data } = useQuery(POST_LIST_QUERY)

  const openPostDetail = (movie) => {
    setPostDetail(movie)
  }

  return (
    <div className="App">
        <Navbar />
        <Switch>
          <ApolloProvider client={apiUserClient}>
            <Route path="/login" component={Login} />
            <Route path="/register" component={Register} />
          </ApolloProvider>
          <ApolloProvider client={apiPostClient}>
            <Route path="/:id" component={() => <PostDetail postDetail={postDetail} />} />
            {(data) ? (
              <Route path="/" component={() => <Posts data={data} openPostDetail={openPostDetail} />} />
              ) : null }
          </ApolloProvider>
        </Switch>
    </div>
  );
}

export default App;
