import React from 'react'
import { Item, Button } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'

import { POST_DELETE_MUTATION } from '../query'

const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const PostDetail = ({ postDetail }) => {
    const history = useHistory()
    const [ postDelete, { data } ] = useMutation(POST_DELETE_MUTATION)
    const user = localStorage.getItem('user')

    const handleOnDelete = () => {
        postDelete({ variables: {id: postDetail.id} })
        history.push('/')
    }

    return (
        <div>
            <Item.Group>
                <Item className="post-detail">
                    <Item.Image size='small' src={DEFAULT_IMAGE} />
                    <Item.Content>
                        <Item.Header>{postDetail.title}</Item.Header>
                        <Item.Meta>User: {postDetail.user}</Item.Meta>
                        <Item.Description className="post-content">{postDetail.content}</Item.Description>
                        {(user === postDetail.user) ? (
                            <div className="post-detail-buttons-container">
                                <Button onClick={handleOnEdit} className="post-detail-edit-button" color="teal">Edit</Button>
                                <Button onClick={handleOnDelete} className="post-detail-delete-button" color="red">Delete</Button>
                            </div>
                        ) : null }
                    </Item.Content>
                </Item>
            </Item.Group>
        </div>
    )
}

export default PostDetail
