import React, { useState, useRef } from 'react'
import { Button, Comment, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'

import { COMMENT_DELETE_MUTATION } from '../api/comment'
import CustomReply from '../Replys/CustomReply'

const DEFAULT_ICON = 'https://react.semantic-ui.com/images/avatar/small/matt.jpg'

const CustomComment = ({ id, replys, content, posted, username }) => {
    const user = localStorage.getItem('user')
    const [ commentDelete ] = useMutation(COMMENT_DELETE_MUTATION)

    const [ allowButton, setAllowButton ] = useState(false)
    const commentRef = useRef()


    const handleOnReply = () => {
        console.log('test')
    }

    const handleOnDelete = () => {
        commentDelete({ variables: { id: id } })
        window.location.reload(false);
    }

    const checkForm = () => {
        if (commentRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }
    
    return (
        <div className="comment-container">
            <Comment>
                <Comment.Avatar src={DEFAULT_ICON} />
                <Comment.Content>
                    <Comment.Author as='a'>{username}</Comment.Author>
                    <Comment.Metadata>
                        <div>{posted}</div>
                    </Comment.Metadata>
                    <Comment.Text>{content}</Comment.Text>
                    <Comment.Actions>
                    <Comment.Action>Reply</Comment.Action>
                    {(user === username) ? (
                        <Comment.Action onClick={handleOnDelete}>Delete</Comment.Action>
                    ) : null}
                    </Comment.Actions>
                </Comment.Content>
                <Comment.Group>
                    <Form onChange={checkForm} reply>
                        <textarea ref={commentRef} className="post-create-content-container" placeholder='Enter something...' />
                        {(allowButton) ? (
                            <Button onClick={handleOnReply} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                        ) : (
                            <Button disabled onClick={handleOnReply} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                        )}
                    </Form>
                </Comment.Group>
                {(replys) ? (
                    <div>
                        {(replys.map(({ id, content, posted, user: { username } }) => {
                            return (
                                <CustomReply 
                                    key={id}
                                    id={id}
                                    content={content}
                                    posted={posted}
                                    username={username}
                                />
                            )
                        }))}
                    </div>
                ) : null}
            </Comment>
        </div>
    )
}

export default CustomComment
