import React, { useState, useEffect } from 'react'
import { Button, Comment, Form } from 'semantic-ui-react'
import { useMutation, useQuery } from '@apollo/react-hooks'
import { COMMENT_DELETE_MUTATION } from '../api/comment'
import { USER_PROFILE_LIST_QUERY } from '../api/user'

import ReplyForm from './ReplyForm'
import Replys from './Replys'
//import styles from './Comments.module.css'

const DEFAULT_ICON = 'https://react.semantic-ui.com/images/avatar/small/matt.jpg'

const CustomComment = ({ id, replys, content, posted, username, image }) => {
    const user = localStorage.getItem('user')
    const [ commentDelete ] = useMutation(COMMENT_DELETE_MUTATION)
    const [ showReplyForm, setShowReplyForm ] = useState(false)

    //If user click on reply -> show the reply form and reverse
    const handleOnReply = () => {
        setShowReplyForm(!showReplyForm)
    }

    const handleOnDelete = () => {
        commentDelete({ variables: { id: id } })

        //reset site
        window.location.reload(false);
    }

    
    return (
        <div className={styles.comment_container}>
            <Comment>
                <Comment.Avatar src={require(`../Profiles/media/${image.image}`)} />
                <Comment.Content>
                    <Comment.Author as='a'><a href={`profile/${username}`}>{username}</a></Comment.Author>
                    <Comment.Metadata>
                        <div>{posted}</div>
                    </Comment.Metadata>
                    <Comment.Text>{content}</Comment.Text>
                    <Comment.Actions>
                    <Comment.Action onClick={handleOnReply}>Reply</Comment.Action>
                    {(user === username) ? (
                        <Comment.Action onClick={handleOnDelete}>Delete</Comment.Action>
                    ) : null}
                    </Comment.Actions>
                </Comment.Content>

                {/*User reply form for comments*/}
                <ReplyForm id={id} showReplyForm={showReplyForm} />
                
                {/*All replys on comment*/}
                {(replys) ? (
                    <Replys replys={replys} />
                ) : null}
            </Comment>
        </div>
    )
}

export default CustomComment
