import React, { useState, useEffect, useRef } from 'react'
import { Button, Form, Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'




const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    const [ allowButton, setAllowButton ] = useState(false)
    const usernameRef = useRef()
    const emailRef = useRef()

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])


    const handleOnSubmit = () => {
        console.log('test')
    }


    const checkForm = () => {
        if (usernameRef.current.value && emailRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    
    return (
        <div className="profile-main-container">
                <div>
                    <div className="profile-container">
                    {console.log(profileData.profileInfo.profile.image)}
                        <img className="profile-picture" src={require(`./media/${profileData.profileInfo.profile.image}`)} />
                        <div className="profile-body">
                            <h2 className="profile-username">{profileData.profileInfo.profile.user.username}</h2>
                            <p className="profile-email">{profileData.profileInfo.profile.user.email}</p>
                        </div>
                    </div>
            {(profileData) ? (
                    <div className="profile-info">
                        <Form onChange={checkForm}>
                            <Form.Field>
                                <label>Username</label>
                                <input className="profile-username" ref={usernameRef} placeholder='Username' />
                            </Form.Field>
                            <Form.Field>
                                <label>Email</label>
                                <input className="profile-email" ref={emailRef} type="password" placeholder='Password' />
                            </Form.Field>
                            {(allowButton) ? (
                                <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Login</Button>
                            ) : (
                                <Button disabled onClick={handleOnSubmit} className="submit-button" type='button' primary>Login</Button>
                            )}
                    </Form>
                </div>
        ) : null }
        </div>
        </div>
    )
}

export default Profile
