import React, { useState, useRef, useEffect } from 'react'
import { Button, Form, Message } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'

import { USER_LOGIN_MUTATION, USER_REFRESH_TOKEN_MUTATION } from '../user_query'

const Login = () => {
    const history = useHistory()

    const [ message, setMessage ] = useState({type: '', text: ''}) 
    const [ buttonActivate, setButtonActivate ] = useState()
    const [ loginUser, { data } ] = useMutation(USER_LOGIN_MUTATION)
    const [ verifyTok ] = useMutation(USER_REFRESH_TOKEN_MUTATION)

    const usernameRef = useRef()
    const passwordRef = useRef()


    useEffect(() => {

            const usernameValue = usernameRef.current.value
            const passwordValue = passwordRef.current.value

            loginUser({ variables: {
                username: usernameValue, password: passwordValue,
            }})
            
            console.log('test')
            
            if (data) {
                const token = data.tokenAuth.token
                const message = data.tokenAuth.success
                
                verifyTok({ variables: {token: token} })

                if (message === false) {
                    setMessage({
                        type: 'error',
                        text: 'Please, enter valid credentials'
                    })
                }

                else {
                    localStorage.setItem('user', usernameValue)
                    localStorage.setItem('token', token)
                    window.location.reload(false);
                    history.push('/')
                }
            }
        }
    ), [buttonActivate == true]

    return (
        <div className="login-container">
            {(message.type) ? (
                <Message
                    className="message-container"
                    error
                    header={message.text}
                />
            ) : null }
            <Form>
                <Form.Field>
                    <label>Username</label>
                    <input ref={usernameRef} placeholder='Username' />
                </Form.Field>
                <Form.Field>
                    <label>Password</label>
                    <input type="password" ref={passwordRef} placeholder='Password' />
                </Form.Field>
                <Button onClick={() => setButtonActivate(true)} className="submit-button" type='button' primary>Login</Button>
            </Form>
        </div>
    )
}

export default Login
