import React, { useRef, useState, useEffect } from 'react'
import { Comment } from 'semantic-ui-react'
import { useMutation, useQuery } from '@apollo/react-hooks'
import { REPLY_DELETE_MUTATION } from '../api/reply'
import { USER_PROFILE_LIST_QUERY } from '../api/user'

const DEFAULT_ICON = 'https://react.semantic-ui.com/images/avatar/small/matt.jpg'

const CustomReply = ({ id, content, posted, username }) => {
    const user = localStorage.getItem('user')
    const [ replyDelete ] = useMutation(REPLY_DELETE_MUTATION)
    const { data: allProfiles } = useQuery(USER_PROFILE_LIST_QUERY)


    const handleOnDelete = () => {
        replyDelete({ variables: { id: id } })
        window.location.reload(false)
    }


    useEffect(() => {
        console.log(username)
    }, [username])

    return (
        <div>
            <Comment.Group>
                <Comment>
                    <Comment.Avatar src={DEFAULT_ICON} />
                    <Comment.Content>
                        <Comment.Author as='a'>{username}</Comment.Author>
                        <Comment.Metadata>
                            <div>{posted}</div>
                        </Comment.Metadata>
                            <Comment.Text>{content}</Comment.Text>
                        <Comment.Actions>
                            {(user === username) ? (
                                <Comment.Action onClick={handleOnDelete}>Delete</Comment.Action>
                            ) : null}
                        </Comment.Actions>
                    </Comment.Content>
                </Comment>
            </Comment.Group>
        </div>
    )
}

export default CustomReply
