import React, { useState, useEffect, useRef } from 'react'
import { Item, Button, Comment, Header, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useParams } from 'react-router-dom'

import { POST_FIND_MUTATION } from './../api/post'
import { COMMENT_ADD_MUTATION } from './../api/comment'

import CustomComment  from '../../Comments/CustomComment'
import PostButtons from './../PostButtons'

const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const PostDetail = () => {
    const { id } = useParams()
    
    const user = localStorage.getItem('user')
    const [ likes, setLikes ] = useState(0)
    
    const [ postFind, { data: detailData } ] = useMutation(POST_FIND_MUTATION)
    const [ commentAdd ] = useMutation(COMMENT_ADD_MUTATION)

    const commentRef = useRef()
    const [ allowButton, setAllowButton ] = useState(false)
    
    useEffect(() => {
        if (detailData) {
            setLikes(detailData.findPost.post.totalLikes)
        }
    }, [detailData])

    useEffect(() => {
        if (id !== 'login' && id !== 'profile') {
            postFind({ variables: { id: id } })
        }
    }, [id])

    const handleOnComment = () => {
        const contentValue = commentRef.current.value
        commentAdd({ variables: { id: id, user: user, content: contentValue } })
        window.location.reload(false);
    }

    const checkForm = () => {
        if (commentRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    return (
        <div>
            {(detailData) ? (
            <Item.Group>
                <Item className="post-detail-item">
                    <Item.Image size='small' src={DEFAULT_IMAGE} />
                    <Item.Content className="post-content">
                        <Item.Header>{detailData.findPost.post.title}</Item.Header>
                        <Item.Meta className="post-user">
                            <a href={`profile/${detailData.findPost.post.user.username}`}>
                                {detailData.findPost.post.user.username}
                            </a>
                        </Item.Meta>
                        <Item.Meta className="post-user">{detailData.findPost.post.posted}</Item.Meta>
                        <Item.Description className="post-textfield">{detailData.findPost.post.content}</Item.Description>
                            {/*Includes like, edit and delete buttons*/}
                            <>
                                <PostButtons id={id} detailData={detailData} likes={likes} />
                            </>
                            <div className="comments-container">
                            {(detailData.findPost.post.commentSet) ? (
                                <Comment.Group>
                                    <Header as='h3' dividing>
                                        Comments
                                    </Header>
                                    {(detailData.findPost.post.commentSet.map(({ id, content, posted, user: { username, profile: image }, replySet }) => {
                                        return (
                                            <CustomComment 
                                                key={id}
                                                id={id}
                                                replys={replySet}
                                                content={content}
                                                posted={posted}
                                                username={username}
                                                image={image}
                                            />
                                        )
                                    }))}
                                </Comment.Group>
                            ) : null }
                            <div className="comment-create-container">
                                <Form onChange={checkForm} reply>
                                    <textarea ref={commentRef} className="post-create-content-container" placeholder='Enter something...' />
                                    {(allowButton) ? (
                                        <Button onClick={handleOnComment} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                                    ) : (
                                        <Button disabled onClick={handleOnComment} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                                    )}
                                </Form>
                            </div>
                        </div>
                    </Item.Content>
                </Item>
            </Item.Group>
        ) : null} 
        </div>
    )
}

export default PostDetail
