import React from 'react'
import { Item } from 'semantic-ui-react'
import { useHistory } from 'react-router-dom'

const User = ({ id, username, email, image }) => {
    const history = useHistory()

    const handleOnClick = () => {
        history.push(`/profile/${username}`)
    }

    return (
        <Item onClick={handleOnClick} className="user-container">
            <Item.Image size='tiny' src={require(`../../Profiles/media/profile_pictures/large/${image}`)} />
            <Item.Content className="user-content-ccontainer">
                <Item.Header>{username}</Item.Header>
                <Item.Description>{email}</Item.Description>
            </Item.Content>
        </Item>
    )
}

export default User
