import React, { useEffect } from 'react'
import '../App.css';
import { useQuery } from '@apollo/react-hooks'

import Post from './Post'
import { POST_LIST_QUERY } from './components/post_query'


const Posts = () => {
    const { data } = useQuery(POST_LIST_QUERY)

    return (
        
        <div className="posts">
            {(data) ? (
                <div>
                    {data.allPosts.map((post, index) => {
                        return (
                            <Post 
                            key={index} 
                            post={post}
                            />
                            )
                        })}
                </div>
            ) : null }
        </div>
    )
}

export default Posts
