import gql from 'graphql-tag'

export const POST_LIST_QUERY = gql`
    query {
        allPosts {
            title
            content
            userId
            id
        }
    }
`

export const USER_LIST_QUERY = gql`
    query {
        allUsers {
            username
            email
        }
    }
`

export const USER_REGISTER_MUTATION = gql`
    mutation createUser (
        $username: String!
        $email: String!
        $password1: String!
            $password2: String!
        ) {
        register (
            username: $username
            email: $email
            password1: $password1
            password2: $password2
        ) {
            success,
            errors,
            token,
            refreshToken
        }
    }
`
