import React from 'react'
import { useParams } from 'react-router-dom'
import { Button, Icon, Popup } from 'semantic-ui-react'

const Message = ({ usersMessage, content, messaged }) => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()

    console.log(messaged)

    return (
        <>
            {/*If this isn't users message*/}
            {usersMessage !== user ? (
                <div className="d-flex my-4">
                    <Popup 
                        content='testik'
                        inverted
                        position="left center"
                        trigger={
                            <div className="py-3 px-3 rounded-pill chat-user-side">
                                <p>{messaged}</p>
                            </div>
                        }
                    />
                </div>
            ) : (
                <div className="d-flex my-4">
                    <Popup
                        content='testik'
                        inverted
                        position="right center"
                        trigger={
                            <div className="ml-auto py-3 px-3 rounded-pill bg-primary">
                                <p className="text-white">{content}</p>
                            </div>
                        }
                    />
                </div>
            )}
       </>
    )
}

export default Message
