import React, { useState, useRef } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'
import { Button, Form, Message } from 'semantic-ui-react'

import { USER_REGISTER_MUTATION } from '../user_query'

//const authenticationList = ['username', 'email', 'password', 'passwordConfirm']

const Register = () => {
  const username = useRef()
  const email = useRef()
  const password = useRef()
  const passwordConfirm = useRef()
  
  const history = useHistory()
  const [ message, setMessage ] = useState({type: '', text: []}) 
  const [ registerUser, { data } ] = useMutation(USER_REGISTER_MUTATION);

  const handleOnSubmit = () => {
    setMessage({type: '', text: []}) //Reset previous state

    const usernameValue = username.current.value
    const emailValue = email.current.value
    const passwordValue = password.current.value
    const passwordConfirmValue = passwordConfirm.current.value

    registerUser({ variables: {
      email: emailValue, username: usernameValue,
      password1: passwordValue, password2: passwordConfirmValue
    }})

    
    if (data) {
      const messageBoolean = data.register.success
      
      if (messageBoolean === false) {
        const errors = Object.entries(data.register.errors)

        errors.map(error => {
          const messageError = error[1][0].message

          return setMessage((prevState) => ({
            type: 'error',
            text: [...prevState.text, messageError]
            
          }))
        })
      }

      else if (messageBoolean === true) {
        history.push('/login')
      }
    }
  }

  return (
    <div className="register-container">
      {(message.type) ? (
        <Message
          className="message-container"
          error
          header="There was some errors with your submission"
          list={message.text}
        />
      ) : null }
      <Form>
        <Form.Field>
          <label>Username</label>
          <input ref={username} placeholder='Username' />
        </Form.Field>
        <Form.Field>
          <label>Email</label>
          <input ref={email} placeholder='Email' />
        </Form.Field>
        <Form.Field>
          <label>Password</label>
          <input type="password" ref={password} placeholder='Password' />
        </Form.Field>
        <Form.Field>
          <label>Confirm Password</label>
          <input type="password" ref={passwordConfirm} placeholder='Confirm Password' />
        </Form.Field>
        <Button onClick={handleOnSubmit} className="register-button" type='button' primary>Register</Button>
      </Form>
    </div>
  )
}

export default Register
