import React, { useState, useEffect, useRef } from 'react'
import { Button, Form, Message } from 'semantic-ui-react'
import { useHistory, useParams } from 'react-router-dom'
import { USER_LIST_QUERY, USER_PROFILE_INFO_MUTATION, USER_UPDATE_MUTATION } from '../api/user'
import { useMutation, useQuery } from '@apollo/react-hooks'

import UploadFile from './UploadFile'


const Profile = () => {
    const { user } = useParams()
    const currentUser = localStorage.getItem('user')

    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)
    const [ profileUpdate, { data: updateData } ] = useMutation(USER_UPDATE_MUTATION)
    const { data: allUsers } = useQuery(USER_LIST_QUERY)

    const [ imageName, setImageName ] = useState()
    const [ errorMessages, setErrorMessages ] = useState()

    const [ allowButton, setAllowButton ] = useState(false)
    const [ usernameValue, setUsernameValue ] = useState('')
    const [ emailValue, setEmailValue ] = useState('')
    const history = useHistory()

    const usernameRef = useRef()
    const emailRef = useRef()
    const [ profileImage, setProfileImage ] = useState('none')

    //Everytime user enters profile -> fetch the profile data
    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])

    //Set the user's info to states (to set default forms)
    useEffect(() => {
        if (profileData) {
            let imagePath = profileData.profileInfo.profile.image
            
            if (imagePath !== 'default.jpg') {
                imagePath = imagePath.slice(17)
            }

            setUsernameValue(profileData.profileInfo.profile.user.username)
            setEmailValue(profileData.profileInfo.profile.user.email)
            setImageName(imagePath)
        }
    }, [profileData])

    //If user updated profile -> check if errors occurs
    useEffect(() => {
        if (updateData) {
            setErrorMessages(updateData.profileUpdate.message)
        }
    }, [updateData])

    //If update was successfull
    useEffect(() => {
        if (updateData !== undefined && updateData.profileUpdate.message === 'Success') {
            localStorage.removeItem('user')
            localStorage.removeItem('token')
            history.push('/login')
            window.location.reload(false)
        }
    }, [updateData])

    //If update button was clicked
    const handleOnSubmit = () => {
        const usernameValue = usernameRef.current.value
        const emailValue = emailRef.current.value

        profileUpdate({ variables: { 
            user: user, newUser: usernameValue,
            newEmail: emailValue, file: profileImage }
        })
    }
    
       
    //Allow button if username and email are filled
    const checkForm = () => {
        if (usernameRef.current.value && emailRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    //Changes state with given value

    const onUsernameValueChange = (value) => {
        setUsernameValue(value)
    }
 
    const onEmailValueChange = (value) => {
        setEmailValue(value)
    }   

    const onImageDrop = (userImage) => {
        setProfileImage(userImage)
    }
    

    {console.log(imageName)}
    
    return (
        <div class="profile-container">
            {(profileData && imageName) ?(
                <div>
                    {(errorMessages) ? (
                        <Message
                            className="message-container"
                            error
                            header='There was some errors with your submission'
                            list={[errorMessages]}
                        />
                    ) : null}
                    <div class="media profile-media">
                        <img className="rounded-circle profile-picture" src={require(`./media/profile_pictures/${profileData.profileInfo.profile.image}`)} />
                        <div className="profile-body ml-5">
                            <h2 className="account-heading">{profileData.profileInfo.profile.user.username}</h2>
                            <p className="text-secondary">{profileData.profileInfo.profile.user.email}</p>
                        </div>
                    </div>
                    {(user === currentUser) ? (
                        <div className="form-group">
                            <p className="profile-info-text">Profile info</p>
                            <hr className="mb-4" />
                            <Form onChange={checkForm}>
                                <Form.Field>
                                    <label>Username</label>
                                    <input onChange={event => onUsernameValueChange(event.target.value)} value={usernameValue} ref={usernameRef} placeholder='Username' />
                                </Form.Field>
                                <Form.Field>
                                    <label>Email</label>
                                    <input onChange={event => onEmailValueChange(event.target.value)} value={emailValue} ref={emailRef} placeholder='Email' />
                                </Form.Field>
                                {(allowButton) ? (
                                    <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Update</Button>
                                ) : (
                                    <Button disabled onClick={handleOnSubmit} className="submit-button" type='button' primary>Update</Button>
                                    )}
                            </Form>
                        </div>
                    ) : null }
                </div>
            ) : null}
        </div>
    )
}

export default Profile
