import React, { useState, useEffect } from 'react'
//import { Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'

import { Container, Row, Col, Image } from 'react-bootstrap';



const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])

    
    return (
        <div className="profile-main-container">
            {(profileData) ? (
                <div className="profile-container">
                    <Image src={require(`./media/${profileData.profileInfo.profile.image}`)} roundedCircle />
                    <img className="profile-picture" size='small' circular />
                    <div className="profile-body">
                        <h2 class="profile-username">username</h2>
                        <p class="profile-email">email</p>
                    </div>
                </div>
            ) : null }
        </div>
    )
}

export default Profile
