import { gql } from "@apollo/client";

//Query


//Mutation
export const MESSAGES_LIST_MUTATION = gql`
    mutation queryUserMessages (input: {$user: String!, $chatUser: String!}) {
        queryUserMessages(input: {user: $user, chatUser: $chatUser}) {
            messages {
                content
                user {
                    username
                }
            }
        }
    }
`
