import React, { useEffect } from 'react'
import { Button, Icon, Label } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'

import { USER_PROFILE_FOLLOW_MUTATION  } from '../Api/user'

const ProfileBodyButtons = ({ profileData: { profileInfo: { profile } } }) => {
    const user = localStorage.getItem('user')
    const history = useHistory()

    //destructuring users total followers and users username
    const { totalFollowers, user: { username: usersProfile } } = profile
    const [ profileFollow, { data: followData }] = useMutation(USER_PROFILE_FOLLOW_MUTATION) 

    useEffect(() => {
        console.log('test')
    }, [followData])

    const handleOnFollow = () => {
        profileFollow({ variables: { follower: user, following: usersProfile } })

         //reset site
        window.location.reload(false);
    }

    const handleOnMessage = () => {
        history.push(`/message/${usersProfile}`)
    }

    return (        
        <div className="ml-5">
            {/*If this is not users profile*/}
            {(user !== usersProfile) ? (
                <div>
                    <Button as='div' labelPosition='right'>
                        <Button onClick={handleOnFollow} color='blue'>
                            <Icon name='user plus' />
                            Follow
                        </Button>
                        <Label as='a' basic color='blue' pointing='left'>
                            {totalFollowers}
                        </Label>
                    </Button>
                    <Button onClick={handleOnMessage} className="ml-4" color='blue'>
                        <Icon name='comment' /> Message
                    </Button>
                </div>
                
            ) : (
                <Button as='div' labelPosition='right'>
                    <Button color='blue'>
                        <Icon name='users' />
                        Followers
                    </Button>
                    <Label as='a' basic color='blue' pointing='left'>
                        {profile.totalFollowers}
                    </Label>
                </Button>
            )}
        </div>
    )
}

export default ProfileBodyButtons
