import React, { useState, useEffect } from 'react'
import { Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'


const Profile = () => {
    const [ imageDirectory, setImageDirectory ] = useState('')
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                console.log(user)
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])

    
    useEffect(() => {
        if (profileData !== undefined) {
            setImageDirectory(`media/${profileData.profileInfo.profile.image}`)
        }
    }, [profileData])

    console.log(imageDirectory)

    return (
        <div className="profile-container">
            {(profileData && imageDirectory !== '') ? (
                <div>
                    <img src="media/profile_pictures/w5.jpg" size='medium' />
                </div>
            ) : null }
        </div>
    )
}

export default Profile
