import React, { useState, useEffect } from 'react'
import { Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'


const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])

    
    return (
        <div className="profile-container">
            {(profileData) ? (
                <div className="content-section">
                    <div className="media">
                        <img className="rounded-circle account-img" src={require(`./media/${profileData.profileInfo.profile.image}`)} />
                        <div className="media-body">
                            <h2 className="account-heading">test</h2>
                            <p className="text-secondary">test</p>
                        </div>
                    </div>
                </div>
            ) : null }
        </div>
    )
}

export default Profile
