import React, { useState, useEffect, useRef } from 'react'
import { Button, Form, Checkbox } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'


const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    const [ allowButton, setAllowButton ] = useState(false)
    const usernameRef = useRef()
    const emailRef = useRef()

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])


    const handleOnSubmit = () => {
        console.log('test')
    }


    const checkForm = () => {
        if (usernameRef.current.value && emailRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    
    return (
        <div class="profile-container">
            {(profileData) ?(
                <div>
                    <div class="media profile-media">
                        <img className="rounded-circle profile-picture" src={require(`./media/${profileData.profileInfo.profile.image}`)} />
                        <div className="profile-body ml-5">
                            <h2 className="account-heading">{profileData.profileInfo.profile.user.username}</h2>
                            <p className="text-secondary">{profileData.profileInfo.profile.user.email}</p>
                        </div>
                    </div>
                    <form method="POST" enctype="multipart/form-data">
                        <fieldset className="form-group">
                            <h4>Ez</h4>
                            <legend className="border-bottom mb-4">Profile Info</legend>
                            <Form>
                                <Form.Field>
                                    <label>Username</label>
                                    <input placeholder='Username' />
                                </Form.Field>
                                <Form.Field>
                                <label>Email</label>
                                    <input placeholder='Email' />
                                </Form.Field>
                                <Button className="submit-button" primary>Update</Button>
                            </Form>
                        </fieldset>
                    </form>
                </div>
            ) : null}
        </div>
    )
}

export default Profile
