import React, { useState, useRef } from 'react'
import { Button, Form, Message } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'

import { USER_LOGIN_MUTATION } from '../query'

const Login = () => {
    const history = useHistory()

    const [ message, setMessage ] = useState({type: '', text: ''}) 
    const [ loginUser, { data } ] = useMutation(USER_LOGIN_MUTATION)

    const usernameRef = useRef()
    const passwordRef = useRef()

    const handleOnSubmit = () => {
        const usernameValue = usernameRef.current.value
        const passwordValue = passwordRef.current.value

        loginUser({ variables: {
            username: usernameValue, password: passwordValue,
        }}) 

        if (data) {
            const message = data.tokenAuth.success

            if (message === false) {
                setMessage({
                    type: 'error',
                    text: data.tokenAuth.errors.nonFieldErrors[0].message
                })
            }

            else {
                history.push('/')
            }

        }
    }

    return (
        <div className="login-container">
            {(message.type) ? (
                <Message
                    className="message-container"
                    error
                    header={message.text}
                />
            ) : null }
            <Form>
                <Form.Field>
                    <label>Username</label>
                    <input ref={usernameRef} placeholder='Username' />
                </Form.Field>
                <Form.Field>
                    <label>Password</label>
                    <input type="password" ref={passwordRef} placeholder='Password' />
                </Form.Field>
                <Button onClick={handleOnSubmit} className="login-submit-button" type='submit' primary>Login</Button>
            </Form>
        </div>
    )
}

export default Login
