import React, { useState, useRef } from 'react'
import { Button, Comment, Header, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'

import { COMMENT_ADD_MUTATION } from '../../api/comment'

//import CustomComment  from '../../Comments/CustomComment'

//import stylesComments from '../../Comments/Comments.module.css'
//import styles from '../Posts.module.css'

const CommentsContainer = ({ id, detailData }) => {
    const user = localStorage.getItem('user')
    const [ commentAdd ] = useMutation(COMMENT_ADD_MUTATION)

    const commentRef = useRef()
    const [ allowButton, setAllowButton ] = useState(false)


    const handleOnComment = () => {
        const contentValue = commentRef.current.value

        commentAdd({ variables: { id: id, user: user, content: contentValue } })
        //reset site
        window.location.reload(false);
    }

    //Check if user filled reply form
    const checkForm = () => {
        if (commentRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    return (
        <div className="comments_container">
            {(detailData.findPost.post.commentSet) ? (
                <Comment.Group>
                    <Header as='h3' dividing>
                        Comments
                    </Header>
                    {(detailData.findPost.post.commentSet.map(({ id, content, posted, user: { username, profile: image }, replySet }) => {
                        return (
                            <CustomComment 
                                key={id}
                                id={id}
                                replys={replySet}
                                content={content}
                                posted={posted}
                                username={username}
                                image={image}
                            />
                        )
                    }))}
                </Comment.Group>
            ) : null }
            <div className="comment-create-container">
                <Form onChange={checkForm} reply>
                    <textarea ref={commentRef} className="post_create_content_container" placeholder='Enter something...' />
                    {(allowButton) ? (
                        <Button onClick={handleOnComment} className="comment_create_button" content='Add Reply' labelPosition='left' icon='edit' primary />
                    ) : (
                        <Button disabled onClick={handleOnComment} className="comment_create_button" content='Add Reply' labelPosition='left' icon='edit' primary />
                    )}
                </Form>
            </div>
        </div>
    )
}

export default CommentsContainer
