import React from 'react'
import { useParams } from 'react-router-dom'
import { useMutation } from '@apollo/react-hooks'
import { Popup } from 'semantic-ui-react'

import { MESSAGE_CREATE_MUTATION } from '../Api/message'

const Message = ({ usersMessage, content, messaged }) => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()

    const [ messageCreate ] = useMutation(MESSAGE_CREATE_MUTATION)

    return (
        <>
            {/*If this isn't users message*/}
            {usersMessage !== user ? (
                <div className="d-flex my-4">
                    <Popup 
                        content={messaged}
                        inverted
                        position="left center"
                        trigger={
                            <div className="py-3 px-3 rounded-pill chat-user-side">
                                <p>{content}</p>
                            </div>
                        }
                    />
                </div>
            ) : (
                <div className="d-flex my-4">
                    <Popup
                        content={messaged}
                        inverted
                        position="right center"
                        trigger={
                            <div className="ml-auto py-3 px-3 rounded-pill bg-primary">
                                <p className="text-white">{content}</p>
                            </div>
                        }
                    />
                </div>
            )}
       </>
    )
}

export default Message
