import React, { useState, useRef, useCallback } from 'react'
import { Button, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory } from 'react-router-dom'

import { POST_CREATE_MUTATION, POST_LIST_QUERY } from '../../Api/post'

//import styles from './Posts.module.css'

const PostCreate = ({ isPublic=false }) => {
    const [ allowButton, setAllowButton ] = useState(false)

    const titleRef = useRef()
    const contentRef = useRef()

    const history = useHistory()
    const [ createPost ] = useMutation(POST_CREATE_MUTATION)
  

    const handleOnSubmit = useCallback(() => {
            const user = localStorage.getItem('user')
            const titleValue = titleRef.current.value
            const contentValue = contentRef.current.value
            
            createPost({ variables: { title: titleValue, content: contentValue, user: user }})
            history.push('/')
        }
    )

    

    //check if title and content were filled
    const checkForm = () => {
        if (titleRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    const [ textareaInput, setTextareaInput ] = useState('')

    console.log(textareaInput)

    return (
        <div className="post-create-container">
            <Form onChange={checkForm}>
                <Form.Field>
                    <label>Title</label>
                    <input ref={titleRef} placeholder='Title' />
                </Form.Field>
                <Form.Field>
                    <label>Content</label>
                    <textarea onChange={(event) => setTextareaInput(event.target.value)} value={textareaInput} className="post-create-content-container" placeholder='Enter something...' />
                </Form.Field>
                {(allowButton) ? (
                    <Button onClick={handleOnSubmit} className="submit-button" primary>Create</Button>
                ) : (
                    <Button disabled onClick={handleOnSubmit} className="submit-button" primary>Create</Button>
                ) }
            </Form>
        </div>
    )
}

export default PostCreate
