import React, { useState, useEffect, useRef } from 'react'
import { Button, Form, Checkbox } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'


const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    const [ allowButton, setAllowButton ] = useState(false)
    const usernameRef = useRef()
    const emailRef = useRef()

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])


    const handleOnSubmit = () => {
        console.log('test')
    }


    const checkForm = () => {
        if (usernameRef.current.value && emailRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    
    return (
        <div class="content-section">
        <div class="media">
            <img class="rounded-circle account-img" src={require(`./media/${profileData.profileInfo.profile.image}`)} />
            <div class="media-body">
                <h2 class="account-heading">ez</h2>
                <p class="text-secondary">ez</p>
            </div>
        </div>
        <form method="POST" enctype="multipart/form-data">
            <fieldset class="form-group">
                <legend class="border-bottom mb-4">Profile Info</legend>
                <Form>
                    <Form.Field>
                    <label>First Name</label>
                    <input placeholder='First Name' />
                    </Form.Field>
                    <Form.Field>
                    <label>Last Name</label>
                    <input placeholder='Last Name' />
                    </Form.Field>
                    <Form.Field>
                    <Checkbox label='I agree to the Terms and Conditions' />
                    </Form.Field>
                    <Button type='submit'>Submit</Button>
                </Form>
            </fieldset>
        </form>
    </div>
    )
}

export default Profile
