import React, { useState, useRef } from 'react'
import { Button, Comment, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'

import { REPLY_ADD_MUTATION, REPLY_DELETE_MUTATION } from '../api/reply'


const ReplyForm = ({ id, username, showReplyForm }) => {
    const [ replyComment ] = useMutation(REPLY_ADD_MUTATION)
    const [ replyDelete ] = useMutation(REPLY_DELETE_MUTATION)

    const [ allowButton, setAllowButton ] = useState(false)
    const replyRef = useRef()

    const handleOnAddReply = () => {
        const contentValue = commentRef.current.value
        replyComment({ variables: { id: id, username: username, content: contentValue } })
        window.location.reload(false);
    }

    const checkForm = () => {
        if (replyRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    return (
        <div>
            {(showReplyForm) ? (
                <Comment.Group>
                    <Form onChange={checkForm} reply>
                        <textarea ref={replyRef} className="post-create-content-container" placeholder='Enter something...' />
                        {(allowButton) ? (
                            <Button onClick={handleOnAddReply} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                        ) : (
                            <Button disabled onClick={handleOnAddReply} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                        )}
                    </Form>
                </Comment.Group>
            ) : null}
        </div>
    )
}

export default ReplyForm
