import React, { useState, useRef } from 'react'
import '../App.css';
import { useQuery } from '@apollo/react-hooks'
import { Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'

import Post from './Post'
import { POST_LIST_QUERY } from '../api/post'
import { POST_FILTER_MUTATION  } from '../api/post'


const Posts = () => {
    const { data } = useQuery(POST_LIST_QUERY)
    const [ postFilter, { data: filteredData }] = useMutation(POST_FILTER_MUTATION)
    const searchRef = useRef()

    const handleOnChange = (event) => {
        const searchValue = searchRef.current.value
        postFilter({ variables: { title: searchValue } })
    }

    return (
        <div className="posts">
            <div className="search-bar-container">
                <Form onKeyPress={handleOnChange}>
                    <Form.Field>
                        <input ref={searchRef} placeholder='Search...' />
                    </Form.Field>
                </Form>
            </div>
            {(filteredData || data) ? (
                <div>
                    {(filteredData) ? (
                        <div>
                            {filteredData.postFilter.filteredPosts.map(({ user: { username }, title, posted, id }) => {
                                return (
                                    <Post 
                                    key={id} 
                                    username={username}
                                    title={title}
                                    posted={posted}
                                    id={id}
                                    />
                                    )
                                })}
                        </div>
                    ) : (
                        <div>
                            {data.allPosts.map(({ user: { username }, title, posted, id }) => {
                                return (
                                    <Post 
                                    key={id} 
                                    username={username}
                                    title={title}
                                    posted={posted}
                                    id={id}
                                    />
                                    )
                                })}
                        </div>
                    )}
                </div>
            ) : null}
            {(data === undefined) ? (
                <h1>Loading....</h1>
            ) : null}
        </div>
    )
}

export default Posts
