import React, { useState, useRef } from 'react'
import { Button, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'

import { POST_EDIT_MUTATION } from '../api/post' 

const PostEdit = () => {    
    const { id } = useParams()
    const titleRef = useRef()
    const contentRef = useRef()

    const history = useHistory()
    const [ postEdit ] = useMutation(POST_EDIT_MUTATION)

    const handleOnSubmit = () => {
        const [ allowButton, setAllowButton ] = useState(false)

        const titleValue = titleRef.current.value
        const contentValue = contentRef.current.value

        postEdit({ variables: { id: id, title: titleValue, content: contentValue } })
        history.push('/')
    }

    const checkForm = () => {
        if (titleRef.current.value && contentRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }
    
    return (
        <div className="post-create-container">
            <Form>
                <Form.Field>
                    <label>Title</label>
                    <input ref={titleRef} placeholder="Title" />
                </Form.Field>
                <Form.Field>
                    <label>Content</label>
                    <textarea ref={contentRef} placeholder='Enter something...' />
                </Form.Field>
                <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Edit</Button>
            </Form>
        </div> 
    )
}

export default PostEdit
