import React, { useState, useEffect } from 'react'
import { Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'

const MEDIA_DIR = 'C:\Users\Admin\programs\django+react\tutorials\graphql_django\backend\graphql_django/media/'

const Profile = () => {
    const [ imageDirectory, setImageDirectory ] = useState()
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                console.log(user)
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])

    useEffect(() => {
        setImageDirectory(`${MEDIA_DIR}/${profileData.profileInfo.profile.image}`)
    }, [profileData])

    console.log(imageDirectory)
    
    return (
        <div className="profile-container">
            {(profileData) ? (
                <div>
                    <Image src={profileData.profileInfo.profile.image} size='medium' circular />
                </div>
            ) : null }
        </div>
    )
}

export default Profile
