import React, { useState } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { useParams } from 'react-router-dom'

import { MESSAGE_CREATE_MUTATION } from '../Api/message'

const MessageCreate = () => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()

    const [ messageInput, setMessageInput ] = useState('')
    const [ messageCreate, { data } ] = useMutation(MESSAGE_CREATE_MUTATION)


    const handleOnSubmit = (event) => {
        event.preventDefault()

        if (event.key === 'Enter' && messageInput !== '') {
            messageCreate({ variables: { user: user, chatUser: chatUser, content: messageInput }})
            setMessageInput('')
        }
    }

    return (
        <div className="mt-5 message-create-container">
            <form>
                <div className="form-group">
                    <input
                        onKeyPress={handleOnSubmit}
                        onChange={(event) => setMessageInput(event.target.value)}
                        value={messageInput}
                        className="form-control form-control-lg rounded-pill"
                        placeholder="Enter a message..." 
                    />
                </div>
            </form>
        </div>
    )
}

export default MessageCreate
