import React, { useRef } from 'react'
import { Button, Form } from 'semantic-ui-react'

const PostEdit = (postEditData) => {
    const { title, content } = postEditData

    const titleRef = useRef()
    const contentRef = useRef()

    const handleOnSubmit = () => {
        console.log('ez')
    }

    return (
        <div className="post-create-container">
            <Form>
                <Form.Field>
                    <label>Title</label>
                    <input value="ez" ref={titleRef} placeholder='Title' />
                </Form.Field>
                <Form.Field>
                    <label>Content</label>
                    <textarea value={content} className="post-create-content-container" ref={contentRef} placeholder='Enter something...' />
                </Form.Field>
                <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Create</Button>
            </Form>
        </div> 
    )
}

export default PostEdit
