import { gql } from "@apollo/client";

export const POST_LIST_QUERY = gql`
    query {
        allPosts {
            title
            content
            user
            id
        }
    }
`

export const USER_LIST_QUERY = gql`
    query {
        allUsers {
            username
            email
        }
    }
`

export const USER_REGISTER_MUTATION = gql`
    mutation registerUser ($email: String!, $username: String!, $password1: String!, $password2: String!) {
        register (email: $email, username: $username, password1: $password1, password2: $password2) {
            success
            errors
            token
        }
    }
`

export const USER_LOGIN_MUTATION = gql`
    mutation loginUser ($username: String!, $password: String!) {
        tokenAuth (username: $username, password: $password) {
            success
            errors
            token
        }   
    }
`

export const USER_REFRESH_TOKEN_MUTATION = gql`
    mutation verifyTok ($token: String!) {
        verifyToken (token: $token) {
            success
        }
    }
`

export const POST_CREATE_MUTATION = gql`
    mutation createPost ($title: String!, $content: String!, $user: String!) {
        addPost(input: {title: $title, content: $content, user: $user}) {
            post {
                title
                content
                user
            }
       }
    }
`

export const POST_DELETE_MUTATION = gql`
    mutation postDelete ($id: ID!) {
        deletePost(input: {id: $id}) {
            object {
                title
            }
        }
    }
`

export const POST_EDIT_MUTATION = gql`
    mutation (id: ID!, $input: String!, $content: String!) {
        editPost(input: {id: $id, title: $title, content: $content}) {
            post {
                title
            }
        }
    }
`
