import React, { useEffect } from 'react'
import { useMutation } from '@apollo/react-hooks'
import { useParams } from 'react-router-dom'

import { MESSAGES_LIST_MUTATION } from '../Api/message'

const MessagesContainer = () => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()

    const [ queryUserMessages, { data: messagesData }] = useMutation(MESSAGES_LIST_MUTATION)

    
    useEffect(() => {
        queryUserMessages({ variables: {user: user, chatUser: chatUser} })
    }, [chatUser])
    

    if (messagesData) {
        console.log(messagesData.queryUserMessages.messages)
    }

    return (
        <div className="chat-room-container">
            {/*If messagesData and user and chat_user messages*/}
            {messagesData && messagesData.queryUserMessages.messages.length !== 0 ? (
                <div>
                    {messagesData.queryUserMessages.messages.map(({ content, user: { username } }) => {
                        <div>
                            {username === chatUser ? (
                                <div className="chat-user-side">
                                    <h1></h1>
                                    <h1></h1>
                                </div>
                            ) : null}
                            <div className="user-side">
                                <h1>Ez</h1>
                                <h1>Ez</h1>
                            </div>
                        </div>
                    })}
                </div>
            ) : null }
        </div>
    )
}

export default MessagesContainer
